import smtplibfrom email.mime.text import MIMETextfrom utils.logger import setup_logginglogger = setup_logging('alerting.log')def send_alert(subject, body, to_email):    try:        from_email = "youremail@example.com"        msg = MIMEText(body)        msg["Subject"] = subject        msg["From"] = from_email        msg["To"] = to_email        with smtplib.SMTP("smtp.example.com", 587) as server:            server.starttls()            server.login(from_email, "yourpassword")            server.sendmail(from_email, [to_email], msg.as_string())        logger.info(f"Alert sent to {to_email}")    except Exception as e:        logger.error(f"Error sending alert: {e}")